import { Recipe } from "../models/recipe.model";

export class RecipeService {
    private recipes: Recipe[] = [
        new Recipe('Tomato soup', 'This is a tomato soup!', 'https://www.inspiredtaste.net/wp-content/uploads/2022/11/Tomato-Soup-Recipe-Video.jpg'),
        new Recipe('Kavada soup', 'This is a tomato soup!', 'https://www.inspiredtaste.net/wp-content/uploads/2022/11/Tomato-Soup-Recipe-Video.jpg')
      ];

      getRecipes(): Recipe[]{
        return this.recipes.slice();
      }
}
